MAGICCONFIG={}


function  getMagicInfo(id)
	if MAGICCONFIG[id]~=nil then
		return MAGICCONFIG[id]
	end
	return false
end;

function getMagicLvel(id,level)
	if MAGICCONFIG[id]~=nil then
		return MAGICCONFIG[id].MagicLevelInfo[level]
	end
	return false
end

